#Verkefni
print("Halló")
tala1=int(input("Sláðu inn tölu 1 ")) #Hér er lesin inn breyta 1
tala2=int(input("Sláðu inn tölu 2 ")) #Hér er lesin inn breyta 2
tala3=int(input("Sláðu inn tölu 3 ")) #Hér er lesin inn breyta 3
#Útskrift
print("Fyrsta talan er:",tala1)
print("Önnur talan er:",tala2)
print("Þriðja talan er:",tala3)
print("Summa talnanna er:" ,tala1+tala2+tala3) 
print("Margfeldni talnanna er:" ,tala1*tala2*tala3)
print("Frádráttur talnanna er:" ,tala1-tala2-tala3)
